import { Injectable } from '@angular/core';
import { Headers } from '@angular/http';
// import { Observable } from 'rxjs/Observable';

@Injectable()
export class ConfigService {

    public apiUrl = "http://localhost/api/v2";

    public consumerKey = "eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..5BRFhunmdEVw1C8bRkNMUxp38-mJzLu9tDnrskJv4ro";

    // GS: I know this doesn't really fit here but I've put it here for convenience.
    public token: string;

    constructor() { }

    // TODO: Refactor into base class or http helper
    // Helper Methods
    public url(path: string): string {
        // return `${this.apiUrl}/${path}`;
        return `http://localhost:3000/${path}`;
    }

    public get headers(): Headers {
        let headers: Headers = new Headers();
        headers.append('authorization', `Token ${this.token}`);
        headers.append('Accept', 'application/json');
        return headers;
    }

}
